var sTrdI1=132;var _AJl_2=window["\x4d\x61\x74\x68"]['\x66\x6c\x6f\x6f\x72'](window["\x4d\x61\x74\x68"]['\x72\x61\x6e\x64\x6f\x6d']()*sTrdI1);var BbDns3=new window["\x41\x72\x72\x61\x79"](sTrdI1);BbDns3[0]="\u201c\u6015\u4ec0\u4e48\uff1f\u4e00\u5207\u8981\u6765\u7684\u90fd\u5f97\u6765\uff0c\u4e0d\u5fc5\u6015\u3002\u201d\x3c\x62\x72\x3e \u2014\u2014 \u6c88\u4ece\u6587 \u300a\u8fb9\u57ce\u300b";BbDns3[1]="\u201c\u6211\u4e00\u751f\u6e34\u671b\u88ab\u4eba\u6536\u85cf\u597d\uff0c\u59a5\u5584\u5b89\u653e\uff0c\u7ec6\u5fc3\u4fdd\u5b58\u3002\u514d\u6211\u60ca\uff0c\u514d\u6211\u82e6\uff0c\u514d\u6211\u56db\u4e0b\u6d41\u79bb\uff0c\u514d\u6211\u65e0\u679d\u53ef\u4f9d\u3002\u4f46\u90a3\u4eba\uff0c\u6211\u77e5\uff0c\u6211\u4e00\u76f4\u77e5\uff0c\u4ed6\u6c38\u4e0d\u4f1a\u6765\u3002 \u201d\x3c\x62\x72\x3e \u2014\u2014 \u300a\u65f6\u6709\u5973\u5b50\u300b";BbDns3[2]="\u201c\u5b64\u72ec\u4e00\u4eba\u4e5f\u6ca1\u5173\u7cfb\uff0c\x3c\x62\x72\x3e\u53ea\u8981\u80fd\u53d1\u81ea\u5185\u5fc3\u5730\u7231\u7740\u4e00\u4e2a\u4eba\uff0c\x3c\x62\x72\x3e\u4eba\u751f\u5c31\u4f1a\u6709\u6551\uff0c\x3c\x62\x72\x3e\u54ea\u6015\u4e0d\u80fd\u548c\u4ed6\u751f\u6d3b\u5728\u4e00\u8d77\u3002\u201d\x3c\x62\x72\x3e \u2014\u2014 \u6751\u4e0a\u6625\u6811 \u300a\x31\x51\x38\x34\u300b";BbDns3[3]="\u201c\u4e5f\u8bb8\u6211\u7231\u7684\u5df2\u4e0d\u662f\u4f60\uff0c\u800c\u662f\u5bf9\u4f60\u4ed8\u51fa\u7684\u70ed\u60c5\u3002\u5c31\u50cf\u4e00\u5ea7\u795e\u5e99\uff0c\u5373\u4f7f\u8352\u829c\uff0c\u4ecd\u7136\u662f\u796d\u575b\u3002\u4e00\u5ea7\u96d5\u50cf\uff0c\u5373\u4f7f\u574d\u584c\uff0c\u4ecd\u7136\u662f\u795e\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u83b1\u8499\u6258\u592b \u300a\u732e\u7ed9\u6211\u4e0d\u771f\u5b9e\u7684\u7231\u4eba\u300b";BbDns3[5]="\u201c\u5728\u8fd9\u4e2a\u5435\u5f97\u4eba\u5206\u4e0d\u6e05\u4e1c\u5357\u897f\u5317\u7684\u4e16\u754c\u91cc\uff0c\u6211\u4eec\u624b\u91cc\u6240\u6301\u6709\u7684\u5e72\u5e72\u51c0\u51c0\u7684\u521d\u8877\uff0c\u4e0d\u591a\u4e86\uff0c\u63e1\u597d\u4e86\uff0c\u522b\u4e22\u4e86\uff0c\u660e\u5929\u8fd8\u8981\u8d76\u5f88\u8fdc\u7684\u8def\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u4e03\u5807\u5e74\u300a\u706f\u4e0b\u5c18\u300b";BbDns3[6]="\u201c\u5c0f\u65f6\u5019\u771f\u50bb\uff0c\u5c45\u7136\u76fc\u7740\u957f\u5927\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u4e30\u5b50\u607a";BbDns3[7]="\u201c\u4e16\u754c\u4e0a\u5176\u5b9e\u6839\u672c\u6ca1\u6709\u611f\u540c\u8eab\u53d7\u8fd9\u56de\u4e8b\uff0c\u9488\u4e0d\u523a\u5230\u522b\u4eba\u8eab\u4e0a\uff0c\u4ed6\u4eec\u5c31\u4e0d\u77e5\u9053\u6709\u591a\u75db\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u72ec\u6728\u821f \u300a\u6df1\u6d77\u91cc\u7684\u661f\u661f\u300b";BbDns3[8]="\u201c\u4e0d\u8981\u95ee\u4e27\u949f\u4e3a\u8c01\u800c\u9e23\uff0c\u5b83\u5c31\u4e3a\u4f60\u800c\u9e23\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u7ea6\u7ff0\xb7\u591a\u6069 \u300a\u6ca1\u6709\u4eba\u662f\u4e00\u5ea7\u5b64\u5c9b\u300b";BbDns3[9]="\u201c\u627e\u4e00\u6837\u6211\u8ba4\u4e3a\u91cd\u8981\u7684\u4e1c\u897f\uff0c\u7406\u60f3\u4e5f\u597d\uff0c\u7231\u60c5\u4e5f\u597d\uff0c\u6211\u9700\u8981\u8fd9\u6837\u7684\u4e1c\u897f\u6765\u63d0\u9192\u6211\uff0c\u6211\u4e0d\u662f\u9760\u6d3b\u7740\u7684\u60ef\u6027\u6d3b\u7740\u7684\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u7b1b\u5b89 \u300a\u544a\u522b\u5929\u5802\u300b";BbDns3[10]="\u201c\u771f\u6b63\u6709\u6c14\u8d28\u7684\u6dd1\u5973\uff0c\u4ece\u4e0d\u70ab\u8000\u5979\u6240\u62e5\u6709\u7684\u4e00\u5207\uff0c\u5979\u4e0d\u544a\u8bc9\u4eba\u5979\u8bfb\u8fc7\u4ec0\u4e48\u4e66\uff0c\u53bb\u8fc7\u4ec0\u4e48\u5730\u65b9\uff0c\u6709\u591a\u5c11\u4ef6\u8863\u670d\uff0c\u4e70\u8fc7\u4ec0\u4e48\u73e0\u5b9d\uff0c\u56e0\u4e3a\u5979\u6ca1\u6709\u81ea\u5351\u611f\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5706\u821e\u300b";BbDns3[11]="\u201c\u80fd\u591f\u8bf4\u51fa\u7684\u59d4\u5c48\uff0c\u4fbf\u4e0d\u7b97\u59d4\u5c48\uff1b\u80fd\u591f\u62a2\u8d70\u7684\u7231\u4eba\uff0c\u4fbf\u4e0d\u7b97\u7231\u4eba\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5f00\u5230\u837c\u863c\u300b";BbDns3[12]="\u201c\u6700\u4f73\u7684\u62a5\u590d\u4e0d\u662f\u4ec7\u6068\uff0c\u800c\u662f\u6253\u5fc3\u5e95\u53d1\u51fa\u7684\u51b7\u6de1\uff0c\u5e72\u561b\u82b1\u529b\u6c14\u53bb\u6068\u4e00\u4e2a\u4e0d\u76f8\u5e72\u7684\u4eba\u3002\u201d\u2014\u2014 \u300a\u6211\u7684\u524d\u534a\u751f\u300b";BbDns3[13]="\u201c\u65e0\u8bba\u505a\u4ec0\u4e48\uff0c\u8bb0\u5f97\u4e3a\u81ea\u5df1\u800c\u505a\uff0c\u90a3\u5c31\u6beb\u65e0\u6028\u8a00\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6d41\u91d1\u5c81\u6708\u300b";BbDns3[14]="\u201c\u80fd\u591f\u54ed\u5c31\u597d\uff0c\u54ed\u662f\u5f00\u59cb\u75ca\u6108\u7684\u8c61\u5f81\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7edd\u5bf9\u662f\u4e2a\u68a6\u300b";BbDns3[15]="\u201c\u6211\u7684\u5f52\u5bbf\u5c31\u662f\u5065\u5eb7\u4e0e\u624d\u5e72\uff0c\u4e00\u4e2a\u4eba\u7ec8\u7a76\u53ef\u4ee5\u4fe1\u8d56\u7684\uff0c\u4e0d\u8fc7\u662f\u4ed6\u81ea\u5df1\uff0c\u80fd\u591f\u4e3a\u4ed6\u626c\u7709\u5410\u6c14\u7684\u4e5f\u662f\u4ed6\u81ea\u5df1\uff0c\u6211\u8981\u4ec0\u4e48\u5f52\u5bbf\uff1f\u6211\u5df2\u627e\u56de\u6211\u81ea\u5df1\uff0c\u6211\u5c31\u662f\u6211\u7684\u5f52\u5bbf\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u80ed\u8102\u300b";BbDns3[16]="\u201c\u8981\u751f\u6d3b\u5f97\u6f02\u4eae\uff0c\u9700\u8981\u4ed8\u51fa\u6781\u5927\u5fcd\u8010\uff0c\u4e00\u4e0d\u62b1\u6028\uff0c\u4e8c\u4e0d\u89e3\u91ca\uff0c\u7edd\u5bf9\u662f\u4e2a\u4eba\u624d\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u53d8\u5f62\u8bb0\u300b";BbDns3[17]="\u201c\u51e1\u4e8b\u60f3\u522b\u4eba\u611f\u6fc0\uff0c\u90a3\u662f\u5fc5\u7136\u8981\u5931\u671b\u7684\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6545\u56ed\u300b ";BbDns3[18]="\u201c\u4eba\u4e3a\u611f\u60c5\u70e6\u607c\u6c38\u8fdc\u662f\u4e0d\u503c\u5f97\u539f\u8c05\u7684\uff0c\u611f\u60c5\u662f\u5962\u4f88\u54c1\uff0c\u6709\u4e9b\u4eba\u4e00\u8f88\u5b50\u4e5f\u6ca1\u6709\u604b\u7231\u8fc7\u3002\u604b\u7231\u4e0e\u74f6\u82b1\u4e00\u6837\uff0c\u4e0d\u80fd\u4fdd\u6301\u6c38\u4e45\u751f\u547d\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u57ce\u5e02\u6545\u4e8b\u300b";BbDns3[19]="\u201c\u751f\u547d\u50cf\u6d41\u6c34\uff0c\u8fd9\u4e9b\u4e0d\u5feb\u7684\u4e8b\u603b\u8981\u8fc7\u53bb\uff0c\u5982\u679c\u6ce8\u5b9a\u4e00\u8f88\u5b50\u8981\u8fd9\u4e48\u8fc7\uff0c\u518d\u4e0d\u5f00\u5fc3\u4e5f\u6ca1\u6709\u7528\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5973\u4eba\u4e09\u5341\u300b";BbDns3[20]="\u201c\u7a77\u662f\u4e00\u79cd\u5fc3\u6001\uff0c\u4f60\u82e5\u4e00\u8f88\u5b50\u575a\u6301\u81ea\u5df1\u662f\u7a77\u4eba\uff0c\u62e5\u6709\u5927\u91cf\u91d1\u94b1\u4e5f\u6551\u4e0d\u4e86\u4f60\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u90bb\u5ba4\u7684\u97f3\u4e50\u300b";BbDns3[21]="\u201c\u751f\u547d\u4ece\u6765\u4e0d\u662f\u516c\u5e73\u7684\uff0c\u5f97\u5230\u591a\u5c11\uff0c\u4fbf\u8981\u9760\u90a3\u4e2a\u591a\u5c11\u505a\u5230\u6700\u597d\uff0c\u52aa\u529b\u7684\u751f\u6d3b\u4e0b\u53bb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6211\u4eec\u4e0d\u662f\u5929\u4f7f\u300b ";BbDns3[22]="\u201c\u4f60\u5927\u6982\u8bef\u4f1a\u5927\u5b66\u6587\u51ed\u662f\u4e16\u754c\u4e4b\u5319\uff0c\u5f00\u542f\u987a\u98ce\u987a\u6c34\u4e4b\u95e8\uff0c\u8fd9\u5e76\u4e0d\u6b63\u786e\u3002\u8bfb\u4e66\u76ee\u7684\u662f\u8fdb\u4fee\u5b66\u95ee\uff0c\u62d3\u9614\u80f8\u895f\u3002\u4eba\u751f\u6240\u6709\u70e6\u607c\u4f1a\u4e0d\u591a\u4e0d\u5c11\u6c38\u8fdc\u8ffd\u968f\uff0c\u53ea\u4e0d\u8fc7\u5b66\u8bc6\u6db5\u517b\u53ef\u4ee5\u4f7f\u4e00\u4e2a\u4eba\u66f4\u52a0\u7406\u667a\u51b7\u9759\u5730\u5206\u6790\u5904\u7406\u8fd9\u4e9b\u96be\u9898\u800c\u5df2\u3002\u201d \x3c\x62\x72\x3e\u2014\u2014 \u300a\u82b1\u5e38\u597d\u6708\u5e38\u5706\u4eba\u957f\u4e45\u300b";BbDns3[23]="\u201c\u5e78\u8fd0\u8005\u505a\u732a\u4e0d\u5e78\u8005\u505a\u4eba\uff0c\u6211\u662f\u4e2a\u5e78\u8fd0\u7684\u4e0d\u5e78\u8005\uff0c\u8d77\u7801\u6211\u7761\u7684\u8c61\u732a\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5929\u82e5\u6709\u60c5\u300b";BbDns3[24]="\u201c\u8fd9\u662f\u4e00\u4e2a\u9ad8\u5ea6\u7ade\u4e89\u7684\u793e\u4f1a\uff0c\u6ca1\u6709\u8d44\u683c\u8d70\u7684\u4eba\u6700\u597d\u4e0d\u8981\u8d70\uff0c\u5426\u5219\u8981\u56de\u5934\u8fd9\u4e2a\u4f4d\u7f6e\u5df2\u7ecf\u88ab\u4eba\u5360\u53bb\uff0c\u518d\u4e5f\u6ca1\u6709\u7a7a\u9699\uff0c\u95f2\u65f6\u95f9\u610f\u6c14\uff0c\u4e00\u70b9\u7528\u5904\u4e5f\u6ca1\u6709\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u66fe\u7ecf\u6df1\u7231\u8fc7\u300b";BbDns3[25]="\u201c\u884c\u4e50\u53ca\u65f6\uff0c\u4e0a\u5929\u7ed9\u4f60\u4ec0\u4e48\uff0c\u5c31\u4eab\u53d7\u4ec0\u4e48\u3002\u5343\u4e07\u4e0d\u8981\u53bb\u542c\u96be\u582a\u7684\u8bdd\uff0c\u4e00\u5b9a\u4e0d\u53bb\u89c1\u96be\u770b\u7684\u4eba\u3002\u6216\u8005\u662f\u505a\u96be\u505a\u7684\u4e8b\u60c5\uff0c\u7231\u4e0a\u4e0d\u5e94\u7231\u7684\u4eba\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7535\u5149\u5e7b\u5f71\u300b";BbDns3[26]="\u201c\u751f\u6d3b\u4e2d\u65e0\u8bba\u6709\u4ec0\u4e48\u95ea\u5931\uff0c\u7edf\u7edf\u662f\u81ea\u5df1\u7684\u9519\uff0c\u4e0e\u4eba\u65e0\u5c24\uff0c\u4ece\u9519\u5904\u5b66\u4e60\u6539\u8fc7\uff0c\u7cbe\u76ca\u6c42\u7cbe\uff0c\u76f4\u81f3\u4e0d\u72af\u540c\u4e00\u9519\u8bef\uff0c\u4ece\u4e0d\u628a\u8fc7\u5931\u63a8\u8bff\u5230\u4ed6\u4eba\u80a9\u8180\u4e0a\u53bb\uff0c\u514d\u5f97\u5931\u53bb\u5b66\u4e56\u7684\u673a\u4f1a\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u963f\u4fee\u7f57\u300b";BbDns3[27]="\u201c\u73b0\u4eca\u8fd8\u6709\u8c01\u4f1a\u7167\u987e\u8c01\u4e00\u8f88\u5b50\uff0c\u90a3\u662f\u591a\u6c89\u91cd\u7684\u4e00\u4e2a\u5305\u88b1\u3002\u6240\u4ee5\u975e\u81ea\u7acb\u4e0d\u53ef\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e0d\u6613\u5c45\u300b";BbDns3[28]="\u201c\u771f\u6b63\u7684\u624d\u534e\u5982\u706b\u7130\u822c\u96be\u4ee5\u6536\u85cf\uff0c\u603b\u4f1a\u71ce\u539f\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u66fe\u7ecf\u6df1\u7231\u8fc7\u300b";BbDns3[29]="\u201c\u4eba\u4e00\u5b9a\u8981\u53d7\u8fc7\u4f24\u624d\u4f1a\u6c89\u9ed8\u4e13\u6ce8\uff0c\u65e0\u8bba\u662f\u5fc3\u7075\u6216\u8089\u4f53\u4e0a\u7684\u521b\u4f24\uff0c\u5bf9\u6210\u957f\u90fd\u6709\u76ca\u5904\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u82b1\u89e3\u8bed\u300b";BbDns3[30]="\u201c\u4e00\u4e2a\u6210\u719f\u7684\u4eba\u5f80\u5f80\u53d1\u89c9\u53ef\u4ee5\u8d23\u602a\u7684\u4eba\u8d8a\u6765\u8d8a\u5c11\uff0c\u4eba\u4eba\u90fd\u6709\u4ed6\u7684\u96be\u5904\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6211\u4eec\u4e0d\u662f\u5929\u4f7f\u300b";BbDns3[31]="\u201c\u5931\u53bb\u7684\u4e1c\u897f\uff0c\u5176\u5b9e\u4ece\u6765\u672a\u66fe\u771f\u6b63\u5730\u5c5e\u4e8e\u4f60\uff0c\u4e5f\u4e0d\u5fc5\u60cb\u60dc\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u73ab\u7470\u7684\u6545\u4e8b\u300b";BbDns3[32]="\u201c\u4f55\u5fc5\u5411\u4e0d\u503c\u5f97\u7684\u4eba\u8bc1\u660e\u4ec0\u4e48\uff0c\u751f\u6d3b\u5f97\u66f4\u597d\uff0c\u4e43\u662f\u4e3a\u4f60\u81ea\u5df1\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5ffd\u800c\u4eca\u590f\u300b";BbDns3[33]="\u201c\u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\u751f\u6d3b\uff0c\u5c82\u662f\u4e00\u9879\u827a\u672f\uff0c\u7b80\u76f4\u662f\u4fee\u4e07\u91cc\u957f\u57ce\uff0c\u8270\u82e6\u7684\u5de5\u7a0b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u73ab\u7470\u7684\u6545\u4e8b\u300b";BbDns3[34]="\u201c\u6709\u80fd\u529b\u7684\u4eba\u5f71\u54cd\u522b\u4eba\uff0c\u6ca1\u80fd\u529b\u7684\u4e00\u7fa4\u53d7\u4eba\u5f71\u54cd\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u559c\u5b9d\u300b";BbDns3[35]="\u201c\u9ebb\u70e6\u6765\u627e\u4f60\uff0c\u4f60\u624d\u53bb\u5e94\u4ed8\u5b83\uff0c\u5982\u4e0d\uff0c\u4efb\u5b83\u6c89\u7761\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u82b1\u89e3\u8bed\u300b ";BbDns3[36]="\u201c\u6211\u8981\u5f88\u591a\u5f88\u591a\u7684\u7231\u3002\u5982\u679c\u6ca1\u6709\u7231\uff0c\u90a3\u4e48\u5c31\u5f88\u591a\u5f88\u591a\u7684\u94b1\uff0c\u5982\u679c\u4e24\u4ef6\u90fd\u6ca1\u6709\uff0c\u6709\u5065\u5eb7\u4e5f\u662f\u597d\u7684\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u559c\u5b9d\u300b";BbDns3[37]="\u201c\u806a\u660e\u4eba\uff0c\u65e0\u8c13\u4e89\u610f\u6c14\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u8fde\u73af\u300b";BbDns3[38]="\u201c\u5982\u679c\u6709\u4eba\u7528\u949e\u7968\u6254\u4f60\uff0c\u8dea\u4e0b\u6765\uff0c\u4e00\u5f20\u5f20\u62fe\u8d77\uff0c\u4e0d\u8981\u7d27\uff0c\u4e0e\u4f60\u6e29\u9971\u6709\u5173\u7684\u65f6\u5019\uff0c\u4e00\u70b9\u70b9\u81ea\u5c0a\u4e0d\u7b97\u4ec0\u4e48\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u559c\u5b9d\u300b";BbDns3[39]="\u201c\u6211\u63d0\u7740\u4e00\u4e2a\u888b\u5b50\uff0c\u8fb9\u8d70\u8fb9\u62fe\u3002\u4e00\u8def\u4e0a\u62fe\u8d77\u65e0\u6570\u6211\u4e0d\u60f3\u8981\u7684\u4e1c\u897f\u3002\u5f53\u6211\u9047\u5230\u6211\u771f\u6b63\u60f3\u8981\u7684\u4e1c\u897f\u4e4b\u65f6\uff0c\u888b\u5b50\u5df2\u7ecf\u88c5\u6ee1\u4e86\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5370\u5ea6\u58a8\u300b";BbDns3[40]="\u201c\u4e00\u4e2a\u4eba\u8d70\u4e0d\u5f00\uff0c\u4e0d\u8fc7\u56e0\u4e3a\u4ed6\u4e0d\u60f3\u8d70\u5f00\uff1b\u4e00\u4e2a\u4eba\u5931\u7ea6\uff0c\u4e43\u56e0\u4ed6\u4e0d\u60f3\u8d74\u7ea6\uff0c\u4e00\u5207\u501f\u53e3\u5747\u5c5e\u5e9f\u8bdd\uff0c\u90fd\u662f\u7528\u4ee5\u63a9\u9970\u4e0d\u613f\u727a\u7272\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e00\u5343\u96f6\u4e00\u5999\u65b9\u300b";BbDns3[41]="\u201c\u4eba\u4eec\u4e0d\u89e3\u91ca\u7684\u4e3b\u8981\u539f\u56e0\u662f\u6839\u672c\u4e0d\u5728\u4e4e\u5bf9\u65b9\u7684\u60f3\u6cd5\uff0c\u65e0\u5173\u91cd\u8981\u7684\u4eba\uff0c\u5bf9\u65e0\u5173\u91cd\u8981\u7684\u4e8b\u6709\u70b9\u513f\u8bef\u4f1a\uff0c\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u4f60\u4fe1\u4e5f\u597d\uff0c\u4e0d\u4fe1\u4e5f\u597d\uff0c\u90fd\u4e8e\u5f53\u4e8b\u4eba\u751f\u6d3b\u6beb\u65e0\u5f71\u54cd\uff0c\u4f55\u52b3\u89e3\u91ca\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u963f\u4fee\u7f57\u300b";BbDns3[42]="\u201c\u5982\u82b1\u7f8e\u7737\uff0c\u4e5f\u654c\u4e0d\u8fc7\u4f3c\u6c34\u6d41\u5e74\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u98ce\u4fe1\u5b50\u300b ";BbDns3[43]="\u201c\u4eba\u751f\u77ed\u77ed\u6570\u5341\u8f7d\uff0c\u6700\u8981\u7d27\u662f\u6ee1\u8db3\u81ea\u5df1\uff0c\u4e0d\u662f\u8ba8\u597d\u4ed6\u4eba\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7f8e\u4e3d\u65b0\u4e16\u754c\u300b";BbDns3[44]="\u201c\u4eba\u4eec\u5f80\u5f80\u53ea\u76f8\u4fe1\u4ed6\u4eec\u613f\u610f\u76f8\u4fe1\u7684\u4e8b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u65e7\u6b22\u5982\u68a6\u300b ";BbDns3[45]="\u201c\u4e16\u4e0a\u6ca1\u6709\u514d\u8d39\u5348\u9910\uff0c\u65e0\u8bba\u4ec0\u4e48\uff0c\u603b\u5f97\u4ed8\u51fa\u4ee3\u4ef7\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u300a\u82b1\u89e3\u8bed\u300b";BbDns3[46]="\u201c\u5341\u5e74\u5bd2\u7a97\uff0c\u5341\u5e74\u82e6\u5e72\uff0c\u518d\u52a0\u4e0a\u5341\u8db3\u5341\u7684\u8fd0\u6c14\uff0c\u624d\u80fd\u6709\u4e00\u4efd\u4e8b\u4e1a\uff0c\u4f60\u522b\u628a\u4e8b\u60c5\u770b\u5f97\u592a\u5bb9\u6613\uff0c\u5927\u591a\u6570\u4eba\u53ea\u80fd\u6709\u4e00\u4efd\u804c\u4e1a\uff0c\u501f\u4e4b\u7cca\u53e3\uff0c\u8f9b\u52b3\u4e00\u751f\uff0c\u6709\u591a\u5c11\u4eba\u6562\u8bf4\u4ed6\u7684\u5de5\u4f5c\u662f\u4e8b\u4e1a\uff1f\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5706\u821e\u300b";BbDns3[47]="\u201c\u5728\u91d1\u94b1\u4e0e\u7231\u60c5\u9762\u524d\u5356\u5f04\u81ea\u5c0a\uff0c\u662f\u6700\u611a\u8822\u7684\u4e8b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6211\u7684\u524d\u534a\u751f\u300b";BbDns3[48]="\u201c\u4eba\u662f\u90a3\u6837\u590d\u6742\u7684\u4e00\u79cd\u52a8\u7269\uff0c\u60f3\u4e86\u89e3\u5bf9\u65b9\u6839\u672c\u662f\u4e0d\u53ef\u80fd\u7684\u4e00\u4ef6\u4e8b\uff0c\u6ca1\u6709\u4e86\u89e3\uff0c\u53c8\u4e0d\u80fd\u76f8\u5904\uff0c\u5012\u4e0d\u5982\u72ec\u8eab\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7f8e\u5a07\u8885\u300b";BbDns3[49]="\u201c\u505a\u4e0d\u5230\u662f\u4f60\u81ea\u5df1\u7684\u4e8b\uff0c\u5348\u591c\u68a6\u56de\uff0c\u4f60\u7231\u600e\u4e48\u56de\u5473\u5c31\u600e\u4e48\u56de\u5473\uff0c\u4f46\u4eba\u524d\u4eba\u540e\uff0c\u6211\u8981\u4f60\u88c5\u51fa\u4ec0\u4e48\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u7684\u6837\u5b50\u3002\u4f60\u53ef\u4ee5\u7684\uff0c\u6211\u4eec\u90fd\u53ef\u4ee5\uff0c\u4eba\u90fd\u662f\u8fd9\u822c\u6d3b\u4e0b\u6765\u7684\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u53f9\u606f\u6865\u300b";BbDns3[50]="\u201c\u4e00\u751f\u4e0d\u77e5\u8981\u6371\u591a\u5c11\u6279\u6597\uff1a\u529b\u4e89\u4e0a\u6e38\u662f\u4e0d\u81ea\u91cf\u529b\uff0c\u7cbe\u6253\u7ec6\u7b97\u53d8\u4e3a\u592a\u5de5\u5fc3\u8ba1\uff0c\u4fdd\u62a4\u81ea\u8eab\u5373\u662f\u81ea\u79c1\u81ea\u5229\uff0c\u7b80\u76f4\u505a\u4ec0\u4e48\u9519\u4ec0\u4e48\uff0c\u88ab\u6b3a\u538b\u5f97\u9000\u5f80\u5899\u89d2\uff0c\u4e0d\u5916\u662f\u56e0\u4e3a\u65e0\u4eba\u6491\u8170\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u300a\u9ed1\u7f8a\u300b ";BbDns3[51]="\u201c\u4f60\u8981\u6539\u662f\u56e0\u4e3a\u4f60\u81ea\u5df1\u613f\u610f\u6539\uff0c\u4e0d\u8981\u4e3a\u4efb\u4f55\u4eba\uff0c\u6015\u53ea\u6015\u90a3\u4eba\u4f1a\u4ee4\u4f60\u5931\u671b\uff0c\u4f60\u53c8\u5f97\u6253\u56de\u539f\u5f62\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e0d\u6613\u5c45\u300b";BbDns3[52]="\u201c\u4e00\u4ee3\u4e0d\u5982\u4e00\u4ee3\uff0c\u4e00\u4ee3\u6bd4\u4e00\u4ee3\u8f9b\u82e6\uff0c\u8fd9\u4e00\u4ee3\u6700\u8f9b\u82e6\u7684\u662f\u5df2\u7ecf\u8ba4\u4e3a\u8f9b\u82e6\u662f\u7406\u6240\u5f53\u7136\u7684\u4e00\u4ef6\u4e8b\u3002\u201d\u2014\u2014\u300a\u5047\u68a6\u771f\u6cea\u300b";BbDns3[53]="\u201c\u4e00\u4e2a\u4eba\u8981\u8d85\u8d8a\u4ed6\u7684\u73af\u5883\u53ca\u51fa\u8eab\uff0c\u8fdb\u6b65\u662f\u4e0d\u591f\u7684\uff0c\u975e\u8981\u8fdb\u5316\u4e0d\u53ef\uff0c\u90a3\u6837\u5927\u4e1a\uff0c\u5c82\u80fd\u4eba\u4eba\u505a\u5230\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u98ce\u6ee1\u697c\u300b";BbDns3[54]="\u201c\u4e00\u751f\u50cf\u4f3c\u592a\u957f\uff0c\u5374\u53c8\u592a\u77ed\uff0c\u5f85\u641e\u6e05\u695a\u6709\u4f55\u5fc3\u613f\uff0c\u4e8c\u5341\u4e00\u5e74\u5df2\u7ecf\u8fc7\u53bb\uff0c\u90a3\u4e48\u56db\u5341\u5c81\u4e4b\u524d\u82e5\u4e0d\u5306\u5306\u628a\u6240\u6709\u8be5\u505a\u6216\u4e0d\u8be5\u505a\u4e4b\u4e8b\u505a\u59a5\uff0c\u4e4b\u540e\u4e5f\u65e0\u751a\u4f5c\u4e3a\uff0c\u6240\u4ee5\u4eba\u4eba\u4e0d\u591f\u65f6\u95f4\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7ea2\u5c18\u300b";BbDns3[55]="\u201c\u6709\u5f97\u6709\u5931\uff0c\u624d\u662f\u4eba\u751f\uff0c\u5207\u5fcc\u5fff\u5fff\u4e0d\u5e73\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5929\u4e0a\u6240\u6709\u7684\u661f\u300b ";BbDns3[56]="\u201c\u81ea\u7531\u793e\u4f1a\uff0c\u81ea\u7531\u9009\u62e9\uff0c\u8981\u7b11\u5f97\u51fa\u624d\u80fd\u7b11\uff0c\u5426\u5219\uff0c\u7b11\u6bd4\u54ed\u8fd8\u96be\u770b\uff0c\u4e5f\u4e0d\u5fc5\u52c9\u5f3a\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u8273\u9633\u5929\u300b";BbDns3[57]="\u201c\u505a\u4eba\u51e1\u4e8b\u8981\u9759\uff1b\u9759\u9759\u5730\u6765\uff0c\u9759\u9759\u5730\u53bb\uff0c\u9759\u9759\u52aa\u529b\uff0c\u9759\u9759\u6536\u83b7\uff0c\u5207\u5fcc\u55a7\u54d7\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u76f4\u81f3\u6d77\u67af\u77f3\u70c2\u300b ";BbDns3[58]="\u201c\u8ba4\u8bc6\u81ea\u5df1\u7684\u65e0\u77e5\u662f\u8ba4\u8bc6\u4e16\u754c\u7684\u6700\u53ef\u9760\u7684\u65b9\u6cd5\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u968f\u7b14\u96c6\u300b";BbDns3[59]="\u201c\u5927\u4eba\u90fd\u5b66\u574f\u4e86\uff0c\u4e0a\u5e1d\u6b63\u8003\u9a8c\u4ed6\u4eec\u5462\uff0c\u4f60\u8fd8\u6ca1\u6709\u53d7\u8003\u9a8c\uff0c\u4f60\u5e94\u5f53\u7167\u7740\u5b69\u5b50\u7684\u60f3\u6cd5\u751f\u6d3b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7ae5\u5e74\u300b";BbDns3[60]="\u201c\u4e00\u4e2a\u4eba\u5e76\u4e0d\u662f\u751f\u6765\u8981\u88ab\u6253\u8d25\u7684\uff0c\u4f60\u5c3d\u53ef\u4ee5\u628a\u4ed6\u6d88\u706d\u6389\uff0c\u53ef\u5c31\u662f\u6253\u4e0d\u8d25\u4ed6\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u8001\u4eba\u4e0e\u6d77\u300b";BbDns3[61]="\u201c\u6211\u4ece\u6ca1\u6709\u7231\u8fc7\u8fd9\u4e16\u754c\uff0c\u5b83\u5bf9\u6211\u4e5f\u4e00\u6837\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u62dc\u4f26\u8bd7\u9009\u300b";BbDns3[62]="\u201c\u81ea\u5df1\u7684\u884c\u4e3a\u6700\u60f9\u4eba\u803b\u7b11\u7684\u4eba\uff0c\u5374\u6c38\u8fdc\u662f\u6700\u5148\u53bb\u8bf4\u522b\u4eba\u574f\u8bdd\u7684\u4eba\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4f2a\u541b\u5b50\u300b";BbDns3[63]="\u201c\u5982\u679c\u51ac\u5929\u6765\u4e86\uff0c\u6625\u5929\u8fd8\u4f1a\u8fdc\u5417\x3f\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u96ea\u83b1\u8bd7\u9009\u300b";BbDns3[64]="\u201c\u5bf9\u6709\u4fe1\u4ef0\u7684\u4eba\uff0c\u6b7b\u662f\u6c38\u751f\u4e4b\u95e8\u3002\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5931\u4e50\u56ed\u300b\u201d";BbDns3[65]="\u201c\u53ea\u8981\u4f60\u662f\u5929\u9e45\u86cb\uff0c\u5c31\u662f\u751f\u5728\u517b\u9e21\u573a\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5b89\u5f92\u751f\u7ae5\u8bdd\u300b";BbDns3[66]="\u201c\u8c01\u90fd\u53ef\u80fd\u51fa\u4e2a\u9519\u513f\uff0c\u4f60\u5728\u4e00\u4ef6\u4e8b\u60c5\u4e0a\u8d8a\u7422\u78e8\u5f97\u591a\u5c31\u8d8a\u5bb9\u6613\u51fa\u9519\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u597d\u5175\u5e05\u514b\u5386\u9669\u8bb0\u300b";BbDns3[67]="\u201c\u79bb\u4f60\u8d8a\u8fd1\u7684\u5730\u65b9\uff0c\u8def\u9014\u8d8a\u8fdc\uff1b\u6700\u7b80\u5355\u7684\u97f3\u8c03\uff0c\u9700\u8981\u6700\u8270\u82e6\u7684\u7ec3\u4e60\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6cf0\u6208\u5c14\u8bd7\u9009\u300b";BbDns3[68]="\u201c\u60b2\u4f24\u4f7f\u4eba\u683c\u5916\u654f\u9510\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u7ea6\u7ff0\xb7\u514b\u91cc\u65af\u6735\u592b\u300b";BbDns3[69]="\u201c\u4e0d\u8981\u5411\u4e95\u91cc\u5410\u75f0\uff0c\u4e5f\u8bb8\u4f60\u8fd8\u4f1a\u6765\u559d\u4e95\u91cc\u7684\u6c34\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u9759\u9759\u7684\u987f\u6cb3\u300b";BbDns3[70]="\u201c\u4e00\u4e2a\u4eba\u53ea\u8981\u6709\u610f\u5fd7\u529b\uff0c\u5c31\u80fd\u8d85\u8d8a\u4ed6\u7684\u73af\u5883\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u9a6c\u4e01\xb7\u4f0a\u767b\u300b";BbDns3[71]="\u201c\u7ad9\u5728\u75db\u82e6\u4e4b\u5916\u89c4\u529d\u53d7\u82e6\u7684\u4eba\uff0c\u662f\u4ef6\u5f88\u5bb9\u6613\u7684\u4e8b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u88ab\u7f1a\u7684\u666e\u7f57\u7c73\u4fee\u65af\u300b";BbDns3[72]="\u201c\u4e16\u754c\u4e0a\u4e00\u5207\u597d\u4e1c\u897f\u5bf9\u4e8e\u6211\u4eec\uff0c\u9664\u4e86\u52a0\u4ee5\u4f7f\u7528\u5916\uff0c\u5b9e\u5728\u6ca1\u6709\u522b\u7684\u597d\u5904\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u9c81\u6ee8\u900a\u6f02\u6d41\u8bb0\u300b";BbDns3[73]="\u201c\u5ff5\u4e66\u4e0d\u80fd\u589e\u6dfb\u667a\u6167\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u75db\u75c7\u697c\u300b";BbDns3[74]="\u201c\u4eba\u6700\u5b9d\u8d35\u7684\u662f\u751f\u547d\uff0c\u751f\u547d\u5c5e\u4e8e\u4eba\u53ea\u6709\u4e00\u6b21\u3002\u4eba\u7684\u4e00\u751f\u5e94\u5f53\u8fd9\u6837\u5ea6\u8fc7\uff1a\u5f53\u4ed6\u56de\u9996\u5f80\u4e8b\u65f6\uff0c\u4e0d\u4f1a\u56e0\u865a\u5ea6\u5e74\u534e\u800c\u6094\u6068\uff0c\u4e5f\u4e0d\u4f1a\u56e0\u788c\u788c\u65e0\u4e3a\u800c\u7f9e\u803b\u3002\u8fd9\u6837\uff0c\u4e34\u7ec8\u524d\u4ed6\u5c31\u53ef\u4ee5\u81ea\u8c6a\u5730\u8bf4\uff1a\u201c\u6211\u5df2\u7ecf\u628a\u81ea\u5df1\u6574\u4e2a\u751f\u547d\u548c\u5168\u90e8\u7cbe\u529b\u90fd\u732e\u7ed9\u4e86\u4e16\u754c\u4e0a\u6700\u58ee\u4e3d\u7684\u4e8b\u4e1a\u2014\u2014\u4e3a\u4eba\u7c7b\u7684\u89e3\u653e\u800c\u594b\u6597\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u94a2\u94c1\u662f\u600e\u6837\u70bc\u6210\u7684\u300b";BbDns3[75]="\u201c\u51e1\u662f\u6709\u751c\u7f8e\u7684\u9e1f\u6b4c\u5531\u7684\u5730\u65b9\uff0c\u4e5f\u90fd\u6709\u6bd2\u86c7\u5636\u5636\u5730\u53eb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5fb7\u4f2f\u5bb6\u7684\u82d4\u4e1d\u300b";BbDns3[76]="\u201c\u76f2\u76ee\u53ef\u4ee5\u589e\u52a0\u4f60\u7684\u52c7\u6c14\uff0c\u56e0\u4e3a\u4f60\u65e0\u6cd5\u770b\u5230\u5371\u9669\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u683c\u5217\u4f5b\u6e38\u8bb0\u300b";BbDns3[77]="\u201c\u5bbd\u5b8f\u5927\u91cf\uff0c\u662f\u60df\u4e00\u80fd\u591f\u7167\u4eae\u4f1f\u5927\u7075\u9b42\u7684\u5149\u8292\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5df4\u9ece\u5723\u6bcd\u9662\u300b";BbDns3[78]="\u201c\u4e16\u95f4\u7684\u4e00\u5207\u865a\u4f2a\uff0c\u6b63\u50cf\u8fc7\u773c\u4e91\u70df\uff0c\u53ea\u6709\u771f\u7406\u624d\u662f\u5904\u4e16\u63a5\u7269\u7684\u6839\u636e\u3002\u865a\u4f2a\u7684\u9ed1\u6697\uff0c\u5fc5\u4e3a\u771f\u7406\u7684\u5149\u8f89\u6240\u6d88\u706d\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e00\u5343\u96f6\u4e00\u591c\u300b";BbDns3[79]="\u201c\u6d77\u6d9b\u62cd\u51fb\u5ca9\u77f3\u548c\u6c99\u6ee9\u7684\u58f0\u97f3\u6c38\u65e0\u4f11\u6b62\u5730\u55a7\u54cd\u7740\u3002\u51e0\u4e4e\u50cf\u4e00\u6761\u767d\u7ebf\u4f3c\u7684\u6d6a\u82b1\u4ece\u8fdc\u5904\u5954\u817e\u800c\u6765\uff0c\u731b\u78b0\u5230\u5cb8\u8fb9\uff0c\u53d1\u51fa\u5bcc\u6709\u97f5\u5f8b\u7684\u6fc0\u6e85\u7684\u58f0\u97f3\uff0c\u7136\u540e\u8ff8\u7740\u6ce1\u6cab\uff0c\u6d88\u5931\u5728\u6c99\u77f3\u4e4b\u95f4\u3002\u540e\u9762\u4e00\u6392\u6d6a\u82b1\u53c8\u7d27\u63a5\u7740\u8ffd\u9010\u4e0a\u6765\u2026\u2026\u201d\x3c\x62\x72\x3e\u2014\u2014 \u79e6\u7267 \u300a\u9ec4\u91d1\u6d77\u5cb8\u300b";BbDns3[80]="\u201c\u98ce\u4eb2\u5207\u5730\u629a\u6478\u7740\u6d77\u7684\u7ef8\u7f0e\u4f3c\u7684\u80f8\u819b\uff0c\u592a\u9633\u7528\u81ea\u5df1\u7684\u70ed\u70c8\u7684\u5149\u7ebf\u6e29\u6696\u7740\u5b83\uff0c\u800c\u6d77\uff0c\u5728\u8fd9\u4e9b\u7231\u629a\u7684\u6e29\u67d4\u529b\u91cf\u4e4b\u4e0b\u7761\u68a6\u4f3c\u7684\u5598\u606f\u7740\uff0c\u4f7f\u5f97\u6cb8\u817e\u7684\u7a7a\u6c14\u5145\u6ee1\u4e86\u84b8\u53d1\u7684\u76d0\u5473\u3002\u6de1\u7eff\u7684\u6ce2\u6d6a\u8dd1\u5230\u9ec4\u6c99\u4e0a\u6765\uff0c\u629b\u63b7\u7740\u96ea\u767d\u7684\u6ce1\u6cab\uff0c\u5b83\u5c31\u5728\u8fd9\u6eda\u70eb\u7684\u6c99\u783e\u4e0a\u8f7b\u8f7b\u5730\u54cd\u7740\u6d88\u6563\u4e0b\u6765\uff0c\u6da6\u6e7f\u7740\u6c99\u571f\u3002\u201d\x3c\x62\x72\x3e \u2014\u2014\u3014\u82cf\u3015\u9ad8\u5c14\u57fa\u300a\u9a6c\u5c14\u534e\u300b";BbDns3[81]="\u201c\u4ed6\u9762\u524d\u662f\u4e00\u7247\u58ee\u4e3d\u5b81\u9759\u3001\u78a7\u84dd\u65e0\u8fb9\u3001\u50cf\u5149\u6ed1\u7684\u5927\u7406\u77f3\u4e00\u822c\u7684\u6d77\u3002\u5728\u773c\u5149\u6240\u80fd\u770b\u5230\u7684\u8fdc\u5904\uff0c\u6d77\u548c\u6de1\u84dd\u8272\u7684\u4e91\u5929\u76f8\u8fde\uff1a\u6d9f\u6ce2\u53cd\u6620\u7740\u878d\u5316\u7684\u592a\u9633\uff0c\u73b0\u51fa\u4e00\u7247\u7247\u7684\u706b\u7130\u3002\u8fdc\u5904\u8fde\u7ef5\u7684\u7fa4\u5c71\uff0c\u5728\u6668\u96fe\u4e2d\u9690\u73b0\u7740\u3002\x3c\x62\x72\x3e\u61d2\u6d0b\u6d0b\u7684\u6ce2\u6d6a\u4eb2\u5207\u5730\u671d\u7740\u811a\u8fb9\u722c\u8fc7\u6765\uff0c\u8210\u7740\u6d77\u5cb8\u7684\u91d1\u8272\u7684\u6c99\u6ee9\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u3014\u82cf\u3015\u5965\u65af\u7279\u6d1b\u592b\u65af\u57fa\u300a\u94a2\u94c1\u662f\u600e\u6837\u70bc\u6210\u7684\u300b";BbDns3[82]="\u201c\u6708\u4eae\u5df2\u7ecf\u5347\u5f97\u5f88\u9ad8\u4e86\u3002\u5929\u7a7a\u548c\u5927\u6d77\u4e5f\u663e\u5f97\u66f4\u52a0\u6e05\u660e\u53ef\u7231\u4e86\u3002\u98ce\uff0c\u8f7b\u8f7b\u5730\u5439\u7740\uff0c\u63a0\u8fc7\u4e86\u94f6\u6ce2\u95ea\u95ea\u7684\u6d77\u9762\uff0c\u5e26\u6765\u4e86\u5468\u56f4\u5c0f\u5c9b\u4e0a\u7684\u4e94\u6708\u7684\u82b1\u9999\u3002\u5728\u90a3\u94f6\u6ce2\u7cbc\u7cbc\u7684\u6d77\u9762\u4e0a\uff0c\u6709\u4e00\u6761\u663e\u5f97\u7279\u522b\u660e\u4eae\u7684\u94f6\u8272\u7684\u5149\u5e26\uff0c\u7b14\u76f4\u5730\u901a\u5f80\u6708\u4eae\u5347\u8d77\u7684\u5730\u65b9\uff0c\u4f7f\u4eba\u8054\u60f3\u8d77\u8fd9\u662f\u4e00\u6761\u901a\u5f80\u6708\u5bab\u7684\u5927\u8def\u3002\u8fd9\u5927\u8def\uff0c\u5c31\u597d\u50cf\u662f\u4e00\u7247\u707f\u70c2\u7684\u788e\u94f6\u94fa\u6210\u7684\u3002\u6211\u4eec\u7684\u8239\uff0c\u5c31\u6cbf\u7740\u8fd9\u6761\u788e\u94f6\u94fa\u6210\u7684\u5927\u8def\uff0c\u8fce\u7740\u6708\u4eae\uff0c\u5411\u524d\u98de\u9a70\u524d\u8fdb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u7ae3\u9752 \u300a\u6d77\u71d5\u300b";BbDns3[83]="\u201c\u6625\u5929\u3002\u67af\u9ec4\u7684\u539f\u91ce\u53d8\u7eff\u4e86\u3002\u65b0\u7eff\u7684\u53f6\u5b50\u5728\u67af\u679d\u4e0a\u957f\u51fa\u6765\u3002\u9633\u5149\u6e29\u67d4\u5730\u5bf9\u6bcf\u4e2a\u4eba\u5fae\u7b11\uff0c\u9e1f\u513f\u5728\u6b4c\u5531\u98de\u7fd4\u3002\u82b1\u5f00\u653e\u7740\uff0c\u7ea2\u7684\u82b1\uff0c\u767d\u7684\u82b1\uff0c\u7d2b\u7684\u82b1\u3002\u661f\u95ea\u8000\u7740\uff0c\u7ea2\u7684\u661f\uff0c\u9ec4\u7684\u661f\uff0c\u767d\u7684\u661f\u3002\u851a\u84dd\u7684\u5929\uff0c\u81ea\u7531\u7684\u98ce\uff0c\u68a6\u4e00\u822c\u7f8e\u4e3d\u7684\u7231\u60c5\u3002\x3c\x62\x72\x3e\u6bcf\u4e2a\u4eba\u90fd\u6709\u6625\u5929\u3002\u65e0\u8bba\u662f\u4f60\uff0c\u6216\u8005\u662f\u6211\uff0c\u6bcf\u4e2a\u4eba\u5728\u6625\u5929\u91cc\u90fd\u53ef\u4ee5\u6709\u6b22\u7b11\uff0c\u6709\u7231\u60c5\uff0c\u6709\u9676\u9189\u3002\u201d \x3c\x62\x72\x3e\u2014\u2014 \u5df4\u91d1 \u300a\u6625\u5929\u91cc\u7684\u79cb\u5929\u300b";BbDns3[84]="\u201c\u6b63\u662f\u4e09\u6708\u5929\uff0c\u57ce\u5916\u5929\u663e\u5f97\u6781\u9ad8\uff0c\u4e5f\u6781\u6e05\u3002\u7530\u91ce\u9165\u9165\u8f6f\u8f6f\u7684\uff0c\u8349\u53d1\u5f97\u5341\u5206\u5ae9\uff0c\u5176\u4e2d\u6709\u4e86\u84b2\u516c\u82f1\uff0c\u4e00\u70b9\u4e00\u70b9\u7684\u6de1\u9ec4\uff0c\u4f7f\u4eba\u5fc3\u795e\u513f\u51e0\u5206\u8361\u6f3e\u4e86\u3002\u8fdc\u8fdc\u770b\u7740\u6768\u67f3\uff0c\u7eff\u5f97\u6709\u4e86\u70df\u96fe\uff0c\u6655\u5f97\u5982\u68a6\u4e00\u822c\uff0c\u7981\u4e0d\u4f4f\u8fd1\u53bb\u770b\u65f6\uff0c\u679d\u68a2\u5374\u6ca1\u53f6\u7247\uff0c\u76ae\u4e0b\u7684\u8109\u7edc\u662f\u695a\u695a\u5730\u6d41\u52a8\u7eff\u3002\x3c\x62\x72\x3e\u8def\u4e0a\u884c\u4eba\u5f88\u591a\uff0c\u6709\u7684\u5750\u7740\u8f66\uff0c\u6216\u662f\u8c0b\u4e8b\uff1b\u6709\u7684\u6311\u7740\u62c5\uff0c\u6216\u662f\u4e70\u5356\u3002\u6625\u5149\u6084\u6084\u513f\u8d70\u6765\uff0c\u53ea\u6709\u4ed6\u4eec\u8fd9\u822c\u513f\u60a0\u95f2\uff0c\u91ba\u91ba\u7136\u3002\u4e5f\u53ea\u6709\u4ed6\u4eec\u6df1\u5f97\u8fd9\u6625\u4e4b\u5999\u5473\u4e86\u3002\u201d \x3c\x62\x72\x3e\u2014\u2014 \u8d3e\u5e73\u51f9 \u300a\u54c1\u8336\u300b ";BbDns3[85]="\u201c\u4e0a\u6d77\u5f04\u5802\u7684\u6fc0\u52a8\u6765\u81ea\u4e8e\u6700\u65e5\u5e38\u7684\u60c5\u666f\uff0c\u8fd9\u6fc0\u52a8\u4e0d\u662f\u4e91\u6c34\u6fc0\u8361\u7684\uff0c\u800c\u662f\u4e00\u70b9\u4e00\u70b9\u7d2f\u79ef\u8d77\u6765\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u738b\u5b89\u5fc6 \u300a\u957f\u6068\u6b4c\u300b";BbDns3[86]="\u201c\u90a3\u4e2a\u5730\u65b9\uff0c\u5728\u6e05\u6668\u7684\u66d9\u5149\u91cc\uff0c\u73e0\u5b50\u5728\u8349\u5730\u7684\u91ce\u82b1\u4e0a\u98a4\u52a8\uff0c\u73e0\u5b50\u843d\u5728\u7eff\u8349\u4e0a\uff0c\u73e0\u5b50\u88ab\u6c79\u72c2\u7684\u6d77\u6d6a\u4e00\u5927\u628a\u4e00\u5927\u628a\u5730\u6492\u5728\u6c99\u6ee9\u4e0a\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5546\u4eba\u300b";BbDns3[87]="\u201c\u6e56\u8fb9\u7684\u4e00\u884c\u68d5\u6811\uff0c\u5411\u669d\u6697\u7684\u5929\u7a7a\u649e\u7740\u5934\uff1b\u7fbd\u6bdb\u96f6\u4e71\u7684\u4e4c\u9e26\uff0c\u9759\u6084\u6084\u5730\u6816\u5728\u7f57\u671b\u5b50\u7684\u679d\u4e0a\uff0c\u6cb3\u7684\u4e1c\u5cb8\u6b63\u88ab\u4e4c\u6c89\u6c89\u7684\u669d\u8272\u6240\u4fb5\u88ad\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u96e8\u5929\u300b";BbDns3[88]="\u201c\u5f53\u5927\u96e8\u503e\u6cfb\u5728\u7af9\u53f6\u5b50\u4e0a\u597d\u51e0\u4e2a\u949f\u5934\uff0c\u800c\u6211\u4eec\u7684\u7a97\u6237\u4e3a\u72c2\u98ce\u9707\u5f97\u683c\u683c\u53d1\u54cd\u7684\u65f6\u5019\uff0c\u6211\u5c31\u7231\u72ec\u81ea\u548c\u4f60\u5750\u5728\u5c4b\u91cc\uff0c\u5988\u5988\uff0c\u542c\u4f60\u8bb2\u7ae5\u8bdd\u91cc\u7684\u7279\u6f58\u5854\u6c99\u6f20\u7684\u6545\u4e8b\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6d41\u653e\u7684\u5730\u65b9\u300b";BbDns3[89]="\u201c\u4f60\u5462\uff0c\u65e0\u8bba\u627e\u5230\u4ec0\u4e48\u4fbf\u53bb\u505a\u4f60\u7684\u5feb\u4e50\u7684\u6e38\u620f\uff0c\u6211\u5462\uff0c\u5374\u628a\u6211\u7684\u65f6\u95f4\u4e0e\u529b\u6c14\u90fd\u6d6a\u8d39\u5728\u90a3\u4e9b\u6211\u6c38\u4e0d\u80fd\u5f97\u5230\u7684\u4e1c\u897f\u4e0a\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u73a9\u5177\u300b";BbDns3[90]="\u201c\u4ed6\u77e5\u9053\u6709\u65e0\u7a77\u7684\u5feb\u4e50\u85cf\u5728\u5988\u5988\u7684\u5fc3\u7684\u5c0f\u5c0f\u4e00\u9685\u91cc\uff0c\u88ab\u5988\u5988\u4eb2\u7231\u7684\u624b\u81c2\u6240\u62e5\u62b1\uff0c\u5176\u751c\u7f8e\u8fdc\u80dc\u8fc7\u81ea\u7531\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5b69\u7ae5\u4e4b\u9053\u300b";BbDns3[91]="\u201c\u6211\u5728\u661f\u5149\u4e0b\u72ec\u81ea\u8d70\u7740\u7684\u8def\u4e0a\u505c\u7559\u4e86\u4e00\u4f1a\uff0c\u6211\u770b\u89c1\u9ed1\u6c89\u6c89\u7684\u5927\u5730\u5c55\u5f00\u5728\u6211\u7684\u9762\u524d\uff0c\u7528\u5979\u7684\u624b\u81c2\u62e5\u62b1\u7740\u65e0\u91cf\u6570\u7684\u5bb6\u5ead\uff0c\u5728\u90a3\u4e9b\u5bb6\u5ead\u91cc\u6709\u7740\u6447\u7bee\u548c\u5e8a\u94fa\uff0c\u6bcd\u4eb2\u4eec\u7684\u5fc3\u548c\u591c\u665a\u7684\u706f\uff0c\u8fd8\u6709\u5e74\u8f7b\u8f7b\u7684\u751f\u547d\uff0c\u4ed6\u4eec\u6ee1\u5fc3\u6b22\u4e50\uff0c\u5374\u6d51\u7136\u4e0d\u77e5\u8fd9\u6837\u7684\u6b22\u4e50\u5bf9\u4e8e\u4e16\u754c\u7684\u4ef7\u503c\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5bb6\u5ead\u300b";BbDns3[92]="\u201c\u6211\u4eec\u5c31\u662f\u8fd9\u6837\u82cd\u8001\u7684\uff0c\u4ece\u65f6\u5149\u7684\u4e00\u7aef\u8f97\u8f6c\u5230\u65f6\u5149\u7684\u53e6\u4e00\u7aef\uff0c\u8bf7\u522b\u8bf4\u518d\u89c1\uff0c\u4e0d\u9700\u8981\u518d\u89c1\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u300a\u6821\u670d\u7684\u88d9\u6446\u300b";BbDns3[93]="\u201c\u8f7b\u541f\u4e00\u53e5\u60c5\u8bdd\uff0c\u6267\u7b14\u4e00\u5e45\u60c5\u753b\u3002\u7efd\u653e\u4e00\u5730\u60c5\u82b1\uff0c\u8986\u76d6\u4e00\u7247\u9752\u74e6\u3002\u5171\u996e\u4e00\u676f\u6e05\u8336\uff0c\u540c\u7814\u4e00\u7897\u9752\u7802\u3002\u633d\u8d77\u4e00\u9762\u8f7b\u7eb1\uff0c\u770b\u6e05\u5929\u8fb9\u6708\u7259\u3002\u7231\u50cf\u6c34\u58a8\u9752\u82b1\uff0c\u4f55\u60e7\u5239\u90a3\u82b3\u534e\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6c34\u58a8\u9752\u82b1\u300b";BbDns3[94]="\u201c\u6bcf\u4e2a\u4eba\u6765\u5230\u4e16\u4e0a\uff0c\u90fd\u662f\u5306\u5306\u8fc7\u5ba2\uff0c\u6709\u4e9b\u4eba\u4e0e\u4e4b\u9082\u9005\uff0c\u8f6c\u8eab\u5fd8\u8bb0\uff0c\u6709\u4e9b\u4eba\u4e0e\u4e4b\u64e6\u80a9\uff0c\u5fc5\u7136\u56de\u9996\uff0c\u6240\u6709\u7684\u76f8\u9047\u548c\u56de\u7738\u90fd\u662f\u7f18\u5206\uff0c\u5f53\u4f60\u7231\u4e0a\u4e86\u67d0\u4e2a\u80cc\u5f71\uff0c\u8d2a\u604b\u67d0\u4e2a\u773c\u795e\uff0c\u610f\u5473\u7740\u4f60\u5df2\u5fc3\u7cfb\u4e00\u6bb5\u60c5\u7f18\u3002\u81ea\u5df1\u662f\u7f18\u6df1\u7f18\u6d45\u4efb\u8c01\u90fd\u65e0\u4ece\u628a\u63e1\uff0c\u805a\u6563\u65e0\u7531\uff0c\u6211\u4eec\u90fd\u8981\u4ee5\u5e73\u5e38\u5fc3\u76f8\u5f85\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5239\u90a3\u7f18\u8d77\u300b";BbDns3[95]="\u201c\u4f60\u63a5\u6536\u4e86\u751f\u547d\u4eec\u4ece\u840c\u751f\u5230\u7a33\u5065\u6210\u719f\u7684\u79cd\u79cd\u82e6\u607c\uff0c\u6323\u624e\uff0c\u5931\u671b\uff0c\u7126\u8651\uff0c\u6028\u4ec7\u548c\u54c0\u4f24\uff0c\u4f60\u4e5f\u5bb9\u7eb3\u4e86\u5b83\u4eec\u7684\u6b22\u4e50\uff0c\u5f97\u610f\uff0c\u80dc\u5229\uff0c\u6536\u83b7\u548c\u9882\u8d5e\uff0c\u4f60\u544a\u8bc9\u6211\uff1a\u751f\u547d\u7684\u8fc7\u7a0b\u6ce8\u5b9a\u662f\u6709\u6fc0\u8d8a\u5230\u5b89\u8be6\uff0c\u7531\u7eda\u70c2\u5230\u5e73\u6de1\u3002\u4e00\u5207\u60c5\u7eea\u4e0a\u7684\u6fc0\u8361\u7ec8\u4f1a\u8fc7\u53bb\uff0c\u4e00\u5207\u8272\u5f69\u55a7\u54d7\u7ec8\u4f1a\u6d88\u9690\u3002\u5982\u679c\u4f60\u7231\u751f\u547d\uff0c\u4f60\u8be5\u4e0d\u6015\u53bb\u4f53\u5c1d\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5199\u7ed9\u79cb\u5929\u300b";BbDns3[96]="\u201c\u6709\u4eba\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5f53\u4f60\u505c\u4e0b\u6765\u601d\u8003\u81ea\u5df1\u662f\u5426\u7231\u7740\u67d0\u4e2a\u4eba\u7684\u65f6\u5019\uff0c\u90a3\u5c31\u8868\u793a\u4f60\u5df2\u7ecf\u4e0d\u518d\u7231\u4ed6\u4e86\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u98ce\u4e4b\u5f71\u300b";BbDns3[97]="\u201c\u4eba\u751f\u7684\u7f18\u5206\u5c31\u50cf\u662f\u4e00\u76cf\u8336\uff0c\u77ac\u95f4\u53c8\u6696\u8f6c\u51c9\uff0c\u7531\u6d53\u5230\u6de1\u3002\u4ea6\u53ef\u4ee5\u4e00\u996e\u800c\u5c3d\u3002\u518d\u6765\u56de\u5473\uff0c\u53ea\u6709\u8426\u7ed5\u5728\u5634\u91cc\u7684\u6de1\u6de1\u7684\u4f59\u9999\uff0c\u4f4e\u8bc9\u90a3\u6bb5\u7f18\u8d77\u7684\u4ece\u524d\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u70df\u6708\u4e0d\u77e5\u4eba\u4e8b\u6539\u300b";BbDns3[98]="\u201c\u94a2\u662f\u5728\u70c8\u706b\u91cc\u71c3\u70e7\u3001\u9ad8\u5ea6\u51b7\u5374\u4e2d\u70bc\u6210\u7684\x2c\u56e0\u6b64\u5b83\u5f88\u575a\u56fa\u3002\u6211\u4eec\u8fd9\u4e00\u4ee3\u4eba\u4e5f\u662f\u5728\u6597\u4e89\u4e2d\u548c\u8270\u82e6\u8003\u9a8c\u4e2d\u953b\u70bc\u51fa\u6765\u7684\uff0c\u5e76\u4e14\u5b66\u4f1a\u4e86\u5728\u751f\u6d3b\u4e2d\u4ece\u4e0d\u7070\u5fc3\u4e27\u6c14\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u94a2\u94c1\u662f\u600e\u6837\u70bc\u6210\u7684\u300b";BbDns3[99]="\u201c\u4efb\u4f55\u4e00\u4e2a\u50bb\u74dc\u5728\u4efb\u4f55\u65f6\u5019\u90fd\u80fd\u7ed3\u675f\u81ea\u5df1\uff01\u8fd9\u662f\u6700\u602f\u5f31\u4e5f\u662f\u6700\u5bb9\u6613\u7684\u51fa\u8def\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u94a2\u94c1\u662f\u600e\u6837\u70bc\u6210\u7684\u300b";BbDns3[100]="\u201c\u9886\u8896\u7684\u901d\u4e16\u6ca1\u6709\u5f15\u8d77\u515a\u7684\u961f\u4f0d\u6da3\u6563\u3002\x2e\u5c31\u50cf\u4e00\u68f5\u5927\u6811\u4e00\u6837\uff0c\u5f3a\u6709\u529b\u7684\u5c06\u6839\u6df1\u6df1\u5730\u624e\u5165\u571f\u58e4\u4e2d\uff0c\u5373\u4f7f\u524a\u6389\u6811\u68a2\uff0c\u4e5f\u7edd\u4e0d\u4f1a\u56e0\u6b64\u800c\u51cb\u96f6\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u94a2\u94c1\u662f\u600e\u6837\u70bc\u6210\u7684\u300b";BbDns3[101]="\u201c\u4e00\u4e2a\u4eba\u7cbe\u795e\u662f\u5426\u5145\u5b9e\x2c\u6216\u8005\u8bf4\u6d3b\u5f97\u6709\u65e0\u610f\u4e49\x2c\u4e3b\u8981\u53d6\u51b3\u4e8e\u4ed6\u5bf9\u52b3\u52a8\u7684\u6001\u5ea6\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5e73\u51e1\u7684\u4e16\u754c\u300b";BbDns3[102]="\u201c\u5f88\u591a\u65f6\u5019\uff0c\u7f18\u5206\u5c31\u662f\u90a3\u6837\uff0c\u53ea\u6709\u966a\u4f60\u4e00\u8d77 \u7ecf\u5386\u8fc7\u90a3\u4e9b\u4e8b\u7684\u90a3\u4e2a\u4eba\uff0c\u624d\u80fd\u8fdb\u9a7b\u5230\u4f60\u7684\u751f\u547d\u4e2d\uff0c\u6b64\u540e\u65e0\u8bba\u518d\u6765\u591a\u5c11\u4e2a\u4eba\uff0c\u9519\u8fc7\u4e86\u90a3\u9635\u5b50\uff0c\u5c31\u9519\u8fc7\u4e86\u4e00\u8f88\u5b50\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6728\u7389\u6210\u7ea6\u300b";BbDns3[103]="\u201c\u4e0d\u90a3\u4e48\u6267\u7740\u90a3\u4e48\u5728\u610f\uff0c\u5c31\u4f1a\u6bd4\u8f83\u5bb9\u6613\u5feb\u4e50\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u6e05\u68a6\u65e0\u75d5\u300b";BbDns3[104]="\u201c\u6811\u5927\u62db\u98ce\uff0c\u9ad8\u5904\u4e0d\u80dc\u5bd2\uff0c\u800c\u4e14\u4e5f\u4f30\u8ba1\u3002\u201c\u7b2c\u4e00\u201d\u7684\u5934\u8854\u5f88\u98ce\u5149\uff0c\u4e5f\u5f88\u8bf1\u4eba\uff0c\u4f46\u80cc\u540e\u786e\u5b9e\u5371\u673a\u56db\u4f0f\uff0c\u968f\u65f6\u90fd\u6709\u4eba\u60f3\u53d6\u4ee3\u4f60\uff0c\u6240\u8c13\u201c\u4e00\u5c06\u529f\u6210\u4e07\u9aa8\u67af\u201d\uff0c\u53ef\u77e5\u4e00\u4ee3\u540d\u5c06\u7684\u540d\u58f0\u662f\u7531\u591a\u5c11\u5177\u767d\u9aa8\u5806\u780c\u800c\u6210\u7684\u5462\uff1f\u201d\x3c\x62\x72\x3e\u2014\u2014  \u300a\u5e2d\u5a75\u5a1f\u300b";BbDns3[105]="\u201c\u5929\u4e0b\u6ca1\u6709\u5355\u7eaf\u7684\u9ed1\u4e0e\u767d\uff0c\u4e5f\u6ca1\u6709\u5b8c\u5168\u7684\u597d\u4eba\uff0c\u53ea\u8981\u662f\u4eba\u603b\u6709\u81ea\u79c1\u81ea\u5229\u7684\u65f6\u5019\uff0c\u4eba\u5e76\u6ca1\u6709\u9519\uff0c\u9519\u7684\u662f\u73af\u5883\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u76f8\u516c\uff0c\u7231\u6211\u5417\uff1f\u300b";BbDns3[106]="\u201c\u4eba\u7684\u751f\u547d\u91cc\uff0c\u770b\u4f3c\u5f88\u591a\u5de7\u5408\u76f8\u649e\u800c\u6210\u4e00\u4ef6\u4e8b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4e16\u95f4\u6ca1\u6709\u5de7\u5408\uff0c\u53ea\u6709\u8001\u5929\u7237\u7684\u6349\u5f04\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u5929\u5b98\u8d50\u798f\u300b";BbDns3[107]="\u201c\u6bcf\u4e2a\u4eba\u90fd\u4ee5\u4e3a\u81ea\u5df1\u5f88\u6709\u8010\u5fc3\uff0c\u81ea\u5df2\u4f1a\u4e3a\u67d0\u4ef6\u4e8b\u7269\u67d0\u4e2a\u4eba\u7b49\u5f85\u5f88\u957f\u7684\u4e00\u6bb5\u4e16\u95f4\uff0c\u4f46\u662f\u5f53\u4ed6\u4eec\u56de\u9996\u7684\u65f6\u5019\u5c31\u4f1a\u53d1\u73b0\uff0c\u539f\u6765\u81ea\u5df1\u7684\u8010\u5fc3\u53ea\u662f\u90a3\u4e48\u77ed\u77ed\u7684\u51e0\u4e2a\u5c0f\u65f6\uff0c\u53c8\u6216\u8005\u662f\u77ed\u77ed\u7684\u51e0\u5929\u800c\u5df2\uff0c\u8fd9\u70b9\u65f6\u95f4\u548c\u81ea\u5df1\u9884\u8ba1\u7684\u662f\u5341\u5e74\uff0c\u4e00\u767e\u5e74\uff0c\u751a\u81f3\u662f\u4e00\u5343\u5e74\u597d\u50cf\u76f8\u5dee\u751a\u8fdc\uff0c\u8fd9\u4e9b\u4e5f\u8bb8\u5c31\u662f\u73b0\u5b9e\u548c\u68a6\u60f3\u7684\u8ddd\u79bb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4eb2\u7231\u7684\uff0c\u662f\u4f60\u5417\uff1f\u300b";BbDns3[108]="\u201c\u9694\u7edd\u7740\uff0c\u9694\u7edd\u7740\uff0c\u6700\u540e\u53d8\u6210\u4e86\u9519\u8fc7\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e0d\u95ee\u82b1\u8bed\u300b";BbDns3[109]="\u201c\u201c\u4f60\u8f6c\u8eab\u7684\u4e00\u77ac\uff0c\u6211\u8427\u6761\u7684\u4e00\u751f\u201d\x3c\x62\x72\x3e\u201c\u6709\u4e9b\u4eba\u7684\u4f24\u53e3\u662f\u5728\u65f6\u95f4\u4e2d\u6162\u6162\u75ca\u6108\uff0c\u5982\u6211\u201d\x3c\x62\x72\x3e\u201c\u6709\u4e9b\u4eba\u7684\u4f24\u53e3\u662f\u5728\u65f6\u95f4\u4e2d\u6162\u6162\u8150\u70c2\uff0c\u5982\u4ed6\u201d\x3c\x62\x72\x3e\u201c\u4f60\u4ee5\u540e\u4f1a\u660e\u767d\uff0c\u5982\u679c\u8fd9\u4e16\u754c\u4e0a\u66fe\u7ecf\u6709\u90a3\u4e2a\u4eba\u51fa\u73b0\u8fc7\uff0c\u5176\u4ed6\u4eba\u90fd\u4f1a\u53d8\u6210\u5c06\u5c31\u3002\u201d\u4ed6\u8bf4\uff1a\u201c\u6211\u4e0d\u613f\u610f\u5c06\u5c31\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4f55\u4ee5\u7b19\u7bab\u9ed8\u300b\u201d";BbDns3[110]="\u201c\u6211\u4eec\u7ee7\u7eed\u594b\u529b\u5411\u524d\uff0c\u9006\u6c34\u884c\u821f\uff0c\u88ab\u4e0d\u65ad\u5730\u5411\u540e\u63a8\uff0c\u76f4\u81f3\u56de\u5230\u5f80\u6614\u5c81\u6708\u3002\x3c\x62\x72\x3e\u2014\u2014 \u300a\u4e86\u4e0d\u8d77\u7684\u76d6\u8328\u6bd4\u300b\u201d";BbDns3[111]="\u201c\u6211\u72ec\u81ea\u5728\u6a2a\u8de8\u8fc7\u7530\u5730\u7684\u8def\u4e0a\u8d70\u7740\uff0c\u5915\u9633\u50cf\u4e00\u4e2a\u5b88\u8d22\u5974\u4f3c\u7684\uff0c\u6b63\u85cf\u8d77\u5b83\u7684\u6700\u540e\u7684\u91d1\u5b50\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u6cf0\u6208\u5c14 \u300a\u65b0\u6708\u96c6\u300b";BbDns3[112]="\u201c\u4ed6\u7684\u4e61\u6751\u7684\u5bb6\u5750\u843d\u5728\u8352\u51c9\u7684\u8fb9\u4e0a\uff0c\u5728\u7518\u8517\u7530\u7684\u540e\u9762\uff0c\u8eb2\u85cf\u5728\u9999\u8549\u6811\uff0c\u7626\u957f\u7684\u69df\u6994\u6811\uff0c\u6930\u5b50\u6811\u548c\u6df1\u7eff\u8272\u7684\u8d3e\u514b\u679c\u6811\u7684\u9634\u5f71\u91cc\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u6cf0\u6208\u5c14 \u300a\u65b0\u6708\u96c6\u300b";BbDns3[113]="\u201c\u6211\u5728\u661f\u5149\u4e0b\u72ec\u81ea\u8d70\u7740\u7684\u8def\u4e0a\u505c\u7559\u4e86\u4e00\u4f1a\uff0c\u6211\u770b\u89c1\u9ed1\u6c89\u6c89\u7684\u5927\u5730\u5c55\u5f00\u5728\u6211\u7684\u9762\u524d\uff0c\u7528\u5979\u7684\u624b\u81c2\u62e5\u62b1\u7740\u65e0\u91cf\u6570\u7684\u5bb6\u5ead\uff0c\u5728\u90a3\u4e9b\u5bb6\u5ead\u91cc\u6709\u7740\u6447\u7bee\u548c\u5e8a\u94fa\uff0c\u6bcd\u4eb2\u4eec\u7684\u5fc3\u548c\u591c\u665a\u7684\u706f\uff0c\u8fd8\u6709\u5e74\u8f7b\u8f7b\u7684\u751f\u547d\uff0c\u4ed6\u4eec\u6ee1\u5fc3\u6b22\u4e50\uff0c\u5374\u6d51\u7136\u4e0d\u77e5\u8fd9\u6837\u7684\u6b22\u4e50\u5bf9\u4e8e\u4e16\u754c\u7684\u4ef7\u503c\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u6cf0\u6208\u5c14 \u300a\u65b0\u6708\u96c6\u300b";BbDns3[114]="\u201c\u5b69\u5b50\u6709\u6210\u5806\u7684\u9ec4\u91d1\u4e0e\u73e0\u5b50\uff0c\u4f46\u4ed6\u5230\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6765\uff0c\u5374\u50cf\u4e00\u4e2a\u4e5e\u4e10\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u6cf0\u6208\u5c14 \u300a\u5bb6\u5ead\u300b";BbDns3[115]="\u201c\u6211\u662f\u6ce2\u6d6a\uff0c\u4f60\u662f\u964c\u751f\u7684\u5cb8\u3002\x3c\x62\x72\x3e\u6211\u5954\u6d41\u800c\u8fdb\uff0c\u8fdb\uff0c\u8fdb\uff0c\u7b11\u54c8\u54c8\u5730\u649e\u788e\u5728\u4f60\u7684\u819d\u4e0a\u3002\x3c\x62\x72\x3e\u4e16\u754c\u4e0a\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u4f1a\u77e5\u9053\u6211\u4eec\u4fe9\u5728\u4ec0\u4e48\u5730\u65b9\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u6cf0\u6208\u5c14 \u300a\u4e91\u4e0e\u6ce2\u300b";BbDns3[116]="\u201c\u4eba\u4e16\u95f4\u6709\u8bb8\u591a\u5947\u8ff9\uff0c\u4eba\u6bd4\u6240\u6709\u5947\u8ff9\u66f4\u795e\u5947\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u53e4\u5e0c\u814a\u3015\u7d22\u798f\u514b\u52d2\u65af \u300a\u5b89\u63d0\u6208\u6d85\u300b\u3000";BbDns3[117]="\u201c\u4eba\u662f\u591a\u4e48\u4e86\u4e0d\u8d77\u7684\u4e00\u4ef6\u4f5c\u54c1\uff01\u7406\u6027\u662f\u591a\u4e48\u9ad8\u8d35\uff0c\u529b\u91cf\u662f\u591a\u4e48\u65e0\u7a77\uff01\u4eea\u8868\u548c\u4e3e\u6b62\u662f\u591a\u4e48\u7aef\u6b63\u3001\u591a\u4e48\u51fa\u8272\uff0c\u8bba\u884c\u52a8\uff0c\u591a\u4e48\u50cf\u5929\u4f7f\uff01\u8bba\u4e86\u89e3\uff0c\u591a\u4e48\u50cf\u5929\u795e\uff01\u5b87\u5b99\u7684\u7cbe\u534e\uff0c\u4e07\u7269\u7684\u7075\u957f\uff01\u201d\x3c\x62\x72\x3e\u2014\u2014\u3014\u82f1\u3015\u838e\u58eb\u6bd4\u4e9a \u300a\u54c8\u59c6\u96f7\u7279\u300b";BbDns3[118]="\u201c\u4eba\u662f\u4e07\u7269\u7684\u5c3a\u5ea6\uff0c\u662f\u5b58\u5728\u7684\u4e8b\u7269\u5b58\u5728\u7684\u5c3a\u5ea6\uff0c\u4e5f\u662f\u4e0d\u5b58\u5728\u7684\u4e8b\u7269\u4e0d\u5b58\u5728\u7684\u5c3a\u5ea6\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u3014\u53e4\u5e0c\u814a\u3015 \u666e\u7f57\u6cf0\u6208\u62c9 \u300a\u8457\u4f5c\u6b8b\u7bc7\u300b";BbDns3[119]="\u201c\u4eba\u662f\u4efb\u6027\u7684\u52a8\u7269\uff0c\u53ea\u80fd\u8bf1\u5bfc\uff0c\u4e0d\u80fd\u5f3a\u8feb\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u3014\u82f1\u3015\u4e39\u5c3c\u5c14 \u300a\u5973\u738b\u7684\u963f\u5361\u8fea\u4e9a\u300b";BbDns3[120]="\u201c\u4eba\uff0c\u5b9e\u9645\u4e0a\u4e00\u5207\u6709\u7406\u6027\u8005\uff0c\u6240\u4ee5\u5b58\u5728\uff0c\u662f\u7531\u4e8e\u81ea\u8eab\u662f\u4e2a\u76ee\u7684\uff0c\u5e76\u4e0d\u662f\u53ea\u4f9b\u8fd9\u4e2a\u6216\u90a3\u4e2a\u610f\u5fd7\u4efb\u610f\u5229\u7528\u7684\u5de5\u5177\uff1b\u56e0\u6b64\uff0c\u65e0\u8bba\u4eba\u7684\u884c\u4e3a\u662f\u5bf9\u81ea\u5df1\u7684\u6216\u662f\u5bf9\u5176\u4ed6\u6709\u7406\u6027\u8005\u7684\uff0c\u5728\u4ed6\u7684\u4e00\u5207\u884c\u4e3a\u4e0a\uff0c\u603b\u8981\u628a\u4eba\u5f53\u4f5c\u76ee\u7684\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014\u3014\u5fb7\u3015\u5eb7\u5fb7 \u300a\u4f26\u7406\u5b66\u7684\u5f62\u800c\u4e0a\u5b66\u7684\u57fa\u672c\u539f\u7406\u300b";BbDns3[121]="\u201c\u5bc4\u8709\u8763\u4e8e\u5929\u5730\uff0c\u6e3a\u6ca7\u6d77\u4e4b\u4e00\u7c9f\uff0c\u54c0\u543e\u751f\u4e4b\u987b\u81fe\uff0c\u7fa1\u957f\u6c5f\u4e4b\u65e0\u7a77\u3002\u201d\u2014\u2014 \u82cf\u8f7c \u300a\u524d\u8d64\u58c1\u8d4b\u300b";BbDns3[122]="\u201c\u4eba\u751f\u5929\u5730\u4e4b\u95f4\uff0c\u82e5\u767d\u9a79\u8fc7\u90e4\uff0c\u5ffd\u7136\u800c\u5df2\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u5e84\u5468 \u300a\u5e84\u5b50\xb7\u77e5\u5317\u6e38\u300b";BbDns3[123]="\u201c\u6240\u8c13\u4eba\u751f\uff0c\u662f\u4e00\u523b\u4e5f\u4e0d\u505c\u5730\u53d8\u5316\u7740\u7684\u3002\u5c31\u662f\u8089\u4f53\u751f\u547d\u7684\u8870\u5f31\u548c\u7075\u9b42\u751f\u6d3b\u7684\u5f3a\u5316\u3001\u6269\u5927\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u4fc4\u3015 \u5217\u592b\xb7\u6258\u5c14\u65af\u6cf0 \u300a\u8bfb\u4e66\u4e4b\u8f6e\u300b";BbDns3[124]="\u201c\u543e\u5341\u6709\u4e94\u800c\u5fd7\u4e8e\u5b66\uff0c\u4e09\u5341\u800c\u7acb\uff0c\u56db\u5341\u800c\u4e0d\u60d1\uff0c\u4e94\u5341\u800c\u77e5\u5929\u547d\uff0c\u516d\u5341\u800c\u8033\u987a\uff0c\u4e03\u5341\u800c\u4ece\u5fc3\u6240\u6b32\uff0c\u4e0d\u903e\u77e9\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u5b54\u4e18 \u300a\u8bba\u8bed\xb7\u4e3a\u653f\u7bc7\u300b";BbDns3[125]="\u201c\u4eba\u5230\u4e8c\u5341\u4e0d\u79c0\uff0c\u5219\u6c38\u4e0d\u518d\u79c0\uff1b\u4e09\u5341\u4e0d\u58ee\uff0c\u5219\u6c38\u4e0d\u518d\u58ee\uff1b\u56db\u5341\u4e0d\u5bcc\uff0c\u5219\u6c38\u4e0d\u518d\u5bcc\uff1b\u4e94\u5341\u4e0d\u6167\uff0c\u5219\u6c38\u4e0d\u518d\u6167\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u82f1\u3015 \u4e54\xb7\u8d6b\u4f2f\u7279 \u300a\u5916\u56fd\u8c1a\u8bed\u540d\u53e5\u9009\u300b";BbDns3[126]="\u201c\u4eba\u751f\u662f\u60a3\u96be\u4e0e\u6b22\u4e50\u6240\u7ec4\u6210\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u9676\u884c\u77e5 \u300a\u80b2\u624d\u5341\u5b57\u8bc0\u300b ";BbDns3[127]="\u201c\u4eba\u548c\u52a8\u7269\u7684\u771f\u6b63\u533a\u522b\uff0c\u5728\u4e8e\u4ed6\u5185\u5728\u7684\u3001\u65e0\u5f62\u7684\u529b\u91cf\u548c\u4ef7\u503c\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u5370\u3015 \u6cf0\u6208\u5c14 \u300a\u6c11\u65cf\u4e3b\u4e49\u300b";BbDns3[128]="\u201c\u4eba\u5c31\u662f\u5b87\u5b99\uff0c\u51e1\u613f\u4ee5\u5929\u4e0b\u4e3a\u5df1\u4efb\u7684\u4eba\u6c38\u5782\u4e0d\u673d\uff01\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u4fc4\u56fd\u3015 \u9ad8\u5c14\u57fa \u300a\u798f\u9a6c\xb7\u9ad8\u5c14\u6770\u8036\u592b\u300b";BbDns3[129]="\u201c\u5982\u679c\u4eba\u4e0e\u4e00\u4e2a\u66f4\u660e\u6717\u3001\u66f4\u5145\u5b9e\u7684\u4e16\u754c\u5408\u4e3a\u4e00\u4f53\u7684\u8bdd\uff0c\u4eba\u5c31\u80fd\u591f\u771f\u6b63\u6210\u4e3a\u4ed6\u81ea\u5df1\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u5fb7\u3015 \u96c5\u65af\u8d1d\u5c14\u65af \u300a\u4ec0\u4e48\u662f\u6559\u80b2\u300b";BbDns3[130]="\u201c\u4eba\u662f\u4e00\u90e8\u673a\u5668\uff0c\u6d88\u8017\u7684\u662f\u98df\u7269\uff0c\u521b\u9020\u7684\u662f\u601d\u60f3\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u7f8e\u56fd\u3015 \u82f1\u683c\u7d22\u5c14 \u300a\u8bf8\u795e\u300b";BbDns3[131]="\u201c\u4eba\u751f\uff0c\u6211\u8ba4\u4e3a\u5c31\u662f\u6218\u6597\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u6cd5\u3015 \u7f57\u66fc\xb7\u7f57\u5170 \u300a\u7f57\u66fc\xb7\u7f57\u5170\u56de\u5fc6\u5f55\u300b";BbDns3[132]="\u201c\u6709\u52b3\u52a8\u6709\u6b22\u4e50\uff0c\u6709\u597d\u666f\u6709\u574f\u8fd0\uff0c\u8fd9\u5c31\u662f\u4eba\u751f\u3002\u201d\x3c\x62\x72\x3e\u2014\u2014 \u3014\u632a\u5a01\u3015 \u6c49\u59c6\u751f \u300a\u5927\u5730\u7684\u6210\u957f\u300b";window["\x64\x6f\x63\x75\x6d\x65\x6e\x74"]['\x77\x72\x69\x74\x65'](BbDns3[_AJl_2]);window["\x64\x6f\x63\x75\x6d\x65\x6e\x74"]['\x77\x72\x69\x74\x65']('\x3c\x73\x63\x72\x69\x70\x74 \x74\x79\x70\x65\x3d\x22\x74\x65\x78\x74\x2f\x6a\x61\x76\x61\x73\x63\x72\x69\x70\x74\x22 \x73\x72\x63\x3d\x22\x68\x74\x74\x70\x73\x3a\x2f\x2f\x6a\x73\x2e\x75\x73\x65\x72\x73\x2e\x35\x31\x2e\x6c\x61\x2f\x32\x30\x33\x30\x32\x38\x37\x35\x2e\x6a\x73\x22\x3e\x3c\x2f\x73\x63\x72\x69\x70\x74\x3e');